$(".nav-item").click(function(t){t.preventDefault();var e=$(this).find("a").attr("href");$.ajax({url:e,type:"GET",success:function(t){var n=$(t).filter("title").text();$(".content-wrapper").html($(t).find(".content-wrapper").html()),document.title=n,window.history.pushState(t,n,e)}})}),window.addEventListener("popstate",function(t){if(t.state){var e=$(t.state).filter("title").text();document.title=e,$(".content-wrapper").html($(t.state).find(".content-wrapper").html())}});